import React from 'react'

const TaskItem = ({todo}) => {
  const {tasks, due, complete} = todo;

  function handleClick(){
    if(todo.complete == false){
      todo.complete = true;
    }
    else{
      todo.complete = false;
    }
    updateTask();
  }

  function updateTask(){
    console.log(todo)
    let id = todo.id
    fetch(`http://localhost:3000/list/${id}`, {
        method: "PUT",
        headers: {
          "Accept": "application/json",
          "Content-Type": "application/json"
        },
        body: JSON.stringify(todo),
      })
      .then(res => res.json())
  }

  return (
    <tr>
      <td id="task" className={todo.complete ? "strike" : ""}>{tasks}</td>
      <td id="due" className={todo.complete ? "strike" : ""}>{due}</td>
      <td>
        <input id="completeCheck" type="checkbox" onChange={handleClick} defaultChecked={complete}/>
      </td>
      <td>
        <button className="btn btn-delete">
          <span className="mdi mdi-delete mdi-24px"></span>
          <span className="mdi mdi-delete-empty mdi-24px"></span>
          <span>Delete</span>
        </button>
      </td>
    </tr>
  )
}

export default TaskItem
