import React from 'react'

const DeleteItem = ({todo, updateTasks}) => {

    function deleteTask(){
        console.log("deleted", todo)
        fetch("http://localhost:3000/list/" + todo.id, {
          method: "DELETE",
          })
          .then(updateTasks())
    }

    return (
        <button className="btn btn-delete" onClick={deleteTask}>
            <span>Remove</span>
        </button>
    );
}

export default DeleteItem
