import React, {useEffect, useState} from 'react';
import TaskItem from './TaskItem';

const TaskList = () => {
  const[todoList, setTodoList] = useState([]);

  useEffect(() =>{
    fetch('http://localhost:3000/list')
      .then(res => res.json())
      .then(data => setTodoList(data))
  }, []);

  const displayTodo = todoList.map((todo) =>{
    return <TaskItem key={todo.id} todo={todo} handleComplete={handleComplete}/>
  })

  const handleComplete = (tasks) => {
    let mapped = todoList.map(task => { //map through the todolist
      return task.tasks == tasks ? {...task, complete: !task.complete} : {...task}; //merges task object. makes complete true or false
    });
    setTodoList(mapped); // new todo list
  }

  return (
    <table className="task-table">
      <thead>
        <tr>
          <th scope="col">Task</th>
          <th scope="col">Due Date</th>
          <th scope="col">Complete</th>
        </tr>
      </thead>
      <tbody>
        {
          displayTodo
        }
      </tbody>
    </table>
  )
}

export default TaskList
